import React, { useState } from "react";

export default function Calculator(){

    const [time, setTime] = useState({"hours": 0, "minutes": 0, "seconds": 0})
    const [distance, setDistance] = useState(0)

    function GetPace(time, distanceTraveled, unit, unitToConvert){
        let timeInSeconds = time.hours * 3600 + time.minutes * 60 + time.seconds;
        let secondsPer = timeInSeconds / distanceTraveled;
    }

    return(
        <div>
            <div>
                <input type="number" onChange={e => setTime({...time, hours: e.target.value})}></input>
                <input type="number" onChange={e => setTime({...time, minutes: e.target.value})}></input>
                <input type="number" onChange={e => setTime({...time, seconds: e.target.value})}></input>
            </div>
            <input type="number" onChange={e => setDistance(e.target.value)}></input>
            <button onClick={() => console.log(`hours: ${time.hours} minutes: ${time.minutes} seconds: ${time.seconds}`)}>Print time</button>
        </div>
    );
}
