import React, { useState } from "react";
import styles from '../styles.module.css'

export default function Calculator(){

    const [time, setTime] = useState({"hours": 0, "minutes": 0, "seconds": 0});
    const [timeText, setTimeText] = useState("");
    const [distance, setDistance] = useState(0);
    const [calculatedPace, setCalculatedPace] = useState({"hours": 0, "minutes": 0, "seconds": 0});

    function GetPace(time, distanceTraveled, unit, unitToConvert){
        let timeInSeconds = time.hours * 3600 + time.minutes * 60 + time.seconds;
        let secondsPer = timeInSeconds / distanceTraveled;

        let hours = Math.floor(secondsPer / 3600);
        let minutes = Math.floor((secondsPer - hours * 3600) / 60);
        let seconds = secondsPer - hours * 3600 - minutes * 60;

        return {"hours": hours, "minutes": minutes, "seconds": seconds};
    }

    const handleOnChange = (e) => {
        let numericalString = e.target.value.replaceAll(":", "");

        let timeString = numericalString.padStart(5, '0')

        let seconds = Number(timeString.slice(-2));
        let minutes = Number(timeString.slice(-4, -2));
        let hours = Number(timeString.slice(0, -4));
        setTime({hours: hours, minutes: minutes, seconds: seconds});

        //console.log({hours: hours, minutes: minutes, seconds: seconds});

        let formattedText = ""

        for(var i = numericalString.length - 1; i >= 0; i--){
            if(i < numericalString.length - 1 && (numericalString.length - i) % 2 == 1){
                formattedText = ":" + formattedText;
            }
            formattedText = numericalString[i] + formattedText;
        }
        if(numericalString.length > 4){
            let lastColon = formattedText.indexOf(":")
            formattedText = formattedText.slice(0, lastColon).replaceAll(":", "") + formattedText.slice(-lastColon);
        }

        setTimeText(formattedText)
    }

    return(
        <div className={styles.container}>
            <input className={styles.timeField} value={timeText} onChange={e => handleOnChange(e)}></input>
            <input type="number" className={styles.inputField} onChange={e => setDistance(e.target.value)}></input>
            <button className={styles.button} onClick={() => setCalculatedPace(GetPace(time, distance, 0, 0))}>Get Pace</button>
        </div>
    );
}
