import React, { useState } from "react";

export default function Calculator(){

    const [time, setTime] = useState({"hours": 0, "minutes": 0, "seconds": 0});
    const [distance, setDistance] = useState(0);
    const [calculatedPace, setCalculatedPace] = useState({"hours": 0, "minutes": 0, "seconds": 0});

    function GetPace(time, distanceTraveled, unit, unitToConvert){
        let timeInSeconds = time.hours * 3600 + time.minutes * 60 + time.seconds;
        let secondsPer = timeInSeconds / distanceTraveled;

        let hours = Math.floor(secondsPer / 3600);
        let minutes = Math.floor((secondsPer - hours * 3600) / 60);
        let seconds = secondsPer - hours * 3600 - minutes * 60;

        return {"hours": hours, "minutes": minutes, "seconds": seconds};
    }

    return(
        <div>
            <div>
                <label htmlFor="fname">Time:</label>
                <input type="number" onChange={e => setTime({...time, hours: Number(e.target.value)})}></input>
                <input type="number" onChange={e => setTime({...time, minutes: Number(e.target.value)})}></input>
                <input type="number" onChange={e => setTime({...time, seconds: Number(e.target.value)})}></input>
            </div>
            <div>
                <label htmlFor="fname">Distance:</label>
                <input type="number" onChange={e => setDistance(e.target.value)}></input>
            </div>
            <button onClick={() => setCalculatedPace(GetPace(time, distance, 0, 0))}>Get Pace</button>
            <b>{`${calculatedPace.hours}:${calculatedPace.minutes}:${calculatedPace.seconds}`}</b>
        </div>
    );
}
